CREATE OR REPLACE FUNCTION proj_obter_dados_rot_item (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_item_w	varchar(255);

/* ie_opcao_p
D - Descrição do item
*/
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ds_item
	into STRICT	ds_item_w
	from	proj_tp_rot_item
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_item_w;
	end if;
end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
