SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION proj_obter_desc_etapa_ident (nr_sequencia_p bigint, nr_nivel_p bigint) RETURNS varchar AS $body$
DECLARE

	ds_atividade_w		varchar(255);
	i					bigint;
	nr_nivel_w			bigint;
	ds_espaco_w			varchar(255);

BEGIN
	ds_espaco_w  := '';
	nr_nivel_w := nr_nivel_p;

	if (coalesce(nr_nivel_w::text, '') = '') then
		nr_nivel_w := proj_obter_nivel_etapa(nr_sequencia_p);
	end if;

	select  trim(both coalesce(max(ds_atividade),'x'))
	into STRICT ds_atividade_w
	from proj_cron_etapa
	where nr_sequencia = nr_sequencia_p;

	for i in 1..nr_nivel_w loop
		ds_espaco_w := ds_espaco_w || '  ';
	end loop;

	ds_atividade_w := substr(ds_espaco_w || ds_atividade_w, 1, 255);

	return ds_atividade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
