CREATE OR REPLACE FUNCTION proj_obter_desc_fase_atu_ger (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(500);


BEGIN

select	max(ds_fase || ' - '|| Obter_valor_dominio(5551, a.ie_area_gerencia))
into STRICT	ds_resultado_w
from	proj_area_gerencia_fase a,
		proj_fase b
where	a.nr_seq_fase = b.nr_sequencia
and		a.nr_sequencia = nr_sequencia_p;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
