CREATE OR REPLACE FUNCTION proj_obter_despesa_consulta ( nr_sequencia_p text, ie_tipo_valor_p text, dt_parametro_inicial_p timestamp, dt_parametro_final_p timestamp, ie_tipo_data_p bigint) RETURNS bigint AS $body$
DECLARE


/* IE_TIPO_VALOR_P
	C - Cliente
*/
vl_retorno_w			double precision	:= 0;
nr_seq_proj_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	proj_projeto
	where	nr_seq_cliente	= nr_sequencia_p
	and	((ie_tipo_data_p = 0) or
		((ie_tipo_data_p = 1) and (trunc(dt_inicio_prev) between trunc(dt_parametro_inicial_p) and fim_dia(dt_parametro_final_p))) or
		((ie_tipo_data_p = 2) and (trunc(dt_fim_prev) between trunc(dt_parametro_inicial_p) and fim_dia(dt_parametro_final_p))) or
		((ie_tipo_data_p = 3) and (trunc(dt_inicio_real) between trunc(dt_parametro_inicial_p) and fim_dia(dt_parametro_final_p))) or
		((ie_tipo_data_p = 4) and (trunc(dt_fim_real) between trunc(dt_parametro_inicial_p) and fim_dia(dt_parametro_final_p))));


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_proj_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	vl_retorno_w	:= vl_retorno_w + coalesce(proj_obter_total_despesa(nr_seq_proj_w, ie_tipo_valor_p),0);
	end;
end loop;
close C01;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
