CREATE OR REPLACE FUNCTION proj_obter_dif_orc_previsto ( nr_seq_orcamento_p bigint) RETURNS bigint AS $body$
DECLARE


total_horas_w	bigint;
nr_seq_proj_w	bigint;
qt_prev_proj_w	double precision;


BEGIN

if (nr_seq_orcamento_p IS NOT NULL AND nr_seq_orcamento_p::text <> '') then

	select	max(nr_seq_proj)
	into STRICT	nr_seq_proj_w
	from	proj_orcamento
	where	nr_sequencia = nr_seq_orcamento_p;

	select	sum(qt_horas)
	into STRICT	total_horas_w
	from	proj_orc_prof
	where	nr_seq_orc = nr_seq_orcamento_p;

	select	sum(qt_total_horas)
	into STRICT	qt_prev_proj_w
	from	proj_cronograma
	where	nr_Seq_proj = nr_seq_proj_w;

end if;

total_horas_w := qt_prev_proj_w - total_horas_w;

return	total_horas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
