SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION proj_obter_ds_projeto_cliente ( nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE


nr_retorno_w	varchar(255);


BEGIN

  select
    substr(a.ds_titulo || ' - ' ||substr(obter_desc_cliente(a.nr_seq_cliente, 'N'),1,255),1,255) INTO STRICT nr_retorno_w
  from
    com_cliente b, 
    proj_projeto a 
  where	 a.nr_seq_cliente = b.nr_sequencia
  and coalesce(a.ie_status,'I') in ('P','E','RP')
  AND a.nr_sequencia = nr_seq_projeto_p;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
