CREATE OR REPLACE FUNCTION proj_obter_margem_mes ( dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE




vl_retorno_w			double precision	:= 0;

vl_cobrar_cliente_w			double precision	:= 0;
vl_pagar_w			double precision	:= 0;
vl_imposto_w			double precision	:= 0;
vl_adicional_clt_w			double precision	:= 0;
vl_custo_inst_w			double precision	:= 0;
vl_margem_w			double precision	:= 0;
dt_inicio_w			timestamp;
dt_fim_w				timestamp;

C01 CURSOR FOR
	SELECT	sum(coalesce(vl_cobrar_cliente,0)) vl_cobrar_cliente,
		sum(coalesce(vl_pagar,0)) vl_pagar,
		sum(coalesce(vl_imposto,0)),
		sum(coalesce(vl_adicional_clt,0)),
		sum(coalesce(vl_custo_inst,0))
	from	proj_rat
	where	dt_aprov_fin	 between	dt_inicio_w and dt_fim_w;


BEGIN

dt_inicio_w	:= trunc(dt_referencia_p,'month');
dt_fim_w		:= last_day(dt_inicio_w) + 86399/86400;

open C01;
loop
fetch C01 into
	vl_cobrar_cliente_w,
	vl_pagar_w,
	vl_imposto_w,
	vl_adicional_clt_w,
	vl_custo_inst_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	vl_pagar_w	:= vl_pagar_w;
end loop;
close C01;

vl_retorno_w	:= vl_cobrar_cliente_w - (vl_pagar_w + vl_imposto_w + vl_adicional_clt_w + vl_custo_inst_w);

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
