CREATE OR REPLACE FUNCTION proj_obter_mediana_realizado (nr_seq_etapa_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	double precision;
qt_total_reg_w	bigint := 0;
qt_hora_etapa_w	double precision;
qt_media_w	bigint;
nr_seq_ordem_w	bigint := 0;
qt_medio_w	bigint;
vl_1_w		bigint;
vl_2_w		bigint;

C01 CURSOR FOR
	SELECT	coalesce(qt_hora_etapa,0)
	from	w_proj_analise_tempos
	where	nr_seq_etapa = nr_seq_etapa_p
	group by qt_hora_etapa,
		nr_seq_proj
	order by 1;


BEGIN

select	Count(*)
into STRICT	qt_total_reg_w
from	w_proj_analise_tempos
where	nr_seq_etapa = nr_seq_etapa_p;

open C01;
loop
fetch C01 into
	qt_hora_etapa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (qt_total_reg_w > 1) then

		nr_seq_ordem_w := nr_seq_ordem_w + 1; --monta a sequência do valor na lista
		qt_media_w := mod(qt_total_reg_w,2); -- verifica se é par ou impar
		if (qt_media_w = 0) then -- Se par, pega os 2 do meio e tira a média deles
			qt_medio_w := qt_total_reg_w/2;
			if (qt_medio_w = nr_seq_ordem_w) then
				vl_1_w := qt_hora_etapa_w;
			end if;
			if (qt_medio_w + 1 = nr_seq_ordem_w) then
				vl_2_w := qt_hora_etapa_w;
				ds_retorno_w := round((vl_1_w + vl_2_w)/2,2);
			end if;

		else -- Se impar, pega o valor do médio.
			qt_media_w := round((qt_total_reg_w/2)::numeric,0); -- Pega o valor do meio
			if (qt_media_w = nr_seq_ordem_w) then -- Compara com a volta do loop. se for igual, pega o campo como mediana
				ds_retorno_w := qt_hora_etapa_w;
			end if;
		end if;
	else
		ds_retorno_w := qt_hora_etapa_w;
	end if;
	end;
end loop;
close C01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
