SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION proj_obter_media_complex ( nr_seq_proj_p bigint) RETURNS bigint AS $body$
DECLARE



qt_itens_w			bigint	:= 0;
qt_soma_w			bigint	:= 0;
vl_retorno_w			bigint	:= 0;



BEGIN

select 	sum(coalesce(b.vl_item,0))
into STRICT	qt_soma_w
from	proj_complexidade a,
	proj_complex_classif_item b
where	a.nr_seq_complex_classif_item = b.nr_sequencia
and	a.nr_seq_proj = nr_seq_proj_p;

select 	count(*)
into STRICT	qt_itens_w
from	proj_complexidade a
where	a.nr_seq_proj = nr_seq_proj_p;


	vl_retorno_w	:= round((dividir(qt_soma_w, qt_itens_w))::numeric,0);

return	vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
