CREATE OR REPLACE FUNCTION proj_obter_modelo_aval (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_modelo_w		varchar(80);
ds_modelo_trad_w varchar(80);

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	nr_sequencia,
		ds_modelo,
    Obter_desc_expressao(CD_EXP_MODELO, DS_MODELO)
	into STRICT	nr_sequencia_w,
		ds_modelo_w,
    ds_modelo_trad_w
	from 	proj_modelo_avaliacao
	where 	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'C')	then
		return nr_sequencia_w;
	elsif (ie_opcao_p = 'T')	then
		return ds_modelo_trad_w;
	elsif (ie_opcao_p = 'D')	then
		return ds_modelo_w;
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
