CREATE OR REPLACE FUNCTION proj_obter_nivel_etapa ( nr_seq_etapa_p bigint) RETURNS bigint AS $body$
DECLARE


i				integer;
k				integer;
cd_classif_conta_w			varchar(255);

BEGIN

i			:= 1;

select	cd_classificacao
into STRICT	cd_classif_conta_w
from	proj_cron_etapa
where	nr_sequencia	= nr_seq_etapa_p;

if (cd_classif_conta_w IS NOT NULL AND cd_classif_conta_w::text <> '') then
	FOR k in 1..length(cd_classif_conta_w) LOOP
		if (substr(cd_classif_conta_w,k,1) = '.') then
			i	:= i + 1;
		end if;
	END LOOP;
end if;
return i;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
