CREATE OR REPLACE FUNCTION proj_obter_nome_padrinho (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

				 
nm_padrinho_w	varchar(200);				
 

BEGIN 
 
select	substr(obter_nome_pf(cd_pessoa_vinculo),1,60) 
into STRICT	nm_padrinho_w 
from	com_canal_consultor	a, 
	pessoa_fisica		b 
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica	 
and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	 
return	nm_padrinho_w;
 
end;
