CREATE OR REPLACE FUNCTION proj_obter_os_pendencia ( nr_seq_pendencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_ordem_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_ordem
	from	proj_ata_pendencia_os
	where	nr_seq_pendencia = nr_seq_pendencia_p
	order by nr_seq_ordem;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_ordem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= ds_retorno_w || to_char(nr_seq_ordem_w) || ', ';
	end;
end loop;
close C01;

return	substr(ds_retorno_w,1,length(ds_retorno_w) - 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
