CREATE OR REPLACE FUNCTION proj_obter_papel_etapa ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_papel_executor_w	PROJ_CRON_ETAPA.IE_PAPEL_EXECUTOR%type;
NR_SEQ_SUPERIOR_w	PROJ_CRON_ETAPA.NR_SEQ_SUPERIOR%type;


BEGIN

select	a.ie_papel_executor,
	a.NR_SEQ_SUPERIOR
into STRICT	ie_papel_executor_w,
	NR_SEQ_SUPERIOR_w
from	PROJ_CRON_ETAPA a
where	a.nr_sequencia = nr_sequencia_p;


if (coalesce(ie_papel_executor_w::text, '') = '') and (NR_SEQ_SUPERIOR_w IS NOT NULL AND NR_SEQ_SUPERIOR_w::text <> '') then
	ie_papel_executor_w	:= proj_obter_papel_etapa(NR_SEQ_SUPERIOR_w);
end if;


return	ie_papel_executor_w;

end;
