CREATE OR REPLACE FUNCTION proj_obter_perc_con_cron (nr_seq_proj_p bigint) RETURNS bigint AS $body$
DECLARE


pr_retorno_w			double precision := 0;
nr_seq_cronograma_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_cronograma_w
from (
	SELECT	z.nr_sequencia,
			z.qt_total_horas
	from	proj_cronograma z
	where 	z.nr_seq_proj = nr_seq_proj_p
	order by
			z.qt_total_horas desc
) x LIMIT 1;

select	max(a.pr_consultoria)
into STRICT	pr_retorno_w
from	proj_cron_etapa a
where 	a.nr_seq_cronograma = nr_seq_cronograma_w
and 	a.ie_fase = 'S'
and 	a.cd_classificacao = '1';

return	pr_retorno_w;

end;
