CREATE OR REPLACE FUNCTION proj_obter_perc_gest_con (nr_seq_modulo_p bigint, nr_seq_gest_con_p bigint, nr_seq_consultor_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p

M = % do módulo
C = % do consultor
A = % da avaliação

*/
qt_reg_nec_rev_w	bigint;
qt_reg_w		bigint;

BEGIN

if (ie_opcao_p = 'M') then

	select	count(*),
		sum(coalesce(pr_conhecimento,0))
	into STRICT	qt_reg_nec_rev_w,
		qt_reg_w
	from	com_cons_gest_con_fun
	where	nr_seq_conhecimento = nr_seq_modulo_p;
end if;

if (ie_opcao_p = 'A') then

	select	count(*),
		sum(coalesce(proj_obter_perc_gest_con(nr_sequencia,null,null,'M'),0))
	into STRICT	qt_reg_nec_rev_w,
		qt_reg_w
	from	com_cons_gest_con_mod
	where	nr_seq_gest_con = nr_seq_gest_con_p;

end if;

if (ie_opcao_p = 'C') then

	select	count(*),
		sum(coalesce(proj_obter_perc_gest_con(nr_sequencia,null,null,'M'),0))
	into STRICT	qt_reg_nec_rev_w,
		qt_reg_w
	from	com_cons_gest_con_mod
	where	nr_seq_gest_con = nr_seq_gest_con_p
	and	nr_seq_consultor = nr_seq_consultor_p;
end if;


return	round((qt_reg_w) / qt_reg_nec_rev_w, 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
