CREATE OR REPLACE FUNCTION proj_obter_perc_imposto_fat ( dt_parametro_p timestamp, nr_seq_pais_p bigint default null) RETURNS bigint AS $body$
DECLARE


pr_retorno_w				double precision;

C01 CURSOR FOR
SELECT	pr_imposto_rec
from	proj_indice_calc
where	dt_parametro_p between dt_inicio_vigencia
and	dt_fim_vigencia
and nr_seq_pais = coalesce(nr_seq_pais_p, nr_seq_pais)
order by dt_inicio_vigencia;


BEGIN
	open C01;
	loop
	fetch C01 into
		pr_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01  */
	end loop;
	close C01;

return pr_retorno_w;

end;
$body$
