CREATE OR REPLACE FUNCTION proj_obter_perc_margem ( nr_seq_projeto_p bigint, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


/* IE_TIPO_VALOR_P
	O - Orçado
	R - Realizado
*/
vl_receita_total_w		double precision	:= 0;
vl_margem_w			double precision	:= 0;
vl_retorno_w			double precision	:= 0;


BEGIN

if (ie_tipo_valor_p	= 'R') then
	/*vl_margem_w	:= proj_obter_valor_margem(nr_seq_projeto_p, ie_tipo_valor_p);
	Conforme solicitado pelo Jair o valor da margem NÃO subtrai o valor de despesa*/
	select	sum(coalesce(a.vl_receita_total,0)),
		sum(coalesce(a.vl_margem,0))
	into STRICT	vl_receita_total_w,
		vl_margem_w
	from	proj_orcamento	b,
		proj_orc_prof	a
	where	a.nr_seq_orc	= b.nr_sequencia
	and	b.nr_seq_proj	= nr_seq_projeto_p
	and	a.ie_tipo_valor	= ie_tipo_valor_p;

	vl_retorno_w	:= dividir((vl_margem_w * 100),vl_receita_total_w);
else
	vl_retorno_w	:= 0;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
