SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION proj_obter_projeto_ordem_serv ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	proj_projeto
	where	ie_origem = 'D'
	and		nr_seq_ordem_serv = nr_sequencia_p;

	if (coalesce(nr_sequencia_w::text, '') = '') then

		select	max(nr_seq_proj)
		into STRICT	nr_sequencia_w
		FROM proj_projeto p
LEFT OUTER JOIN proj_ordem_servico pos ON (p.nr_sequencia = pos.nr_seq_proj)
WHERE p.ie_origem = 'D' and nr_seq_ordem = nr_sequencia_p;

	end if;


	if (coalesce(nr_sequencia_w::text, '') = '') then

		select	max(d.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	proj_projeto d,
			proj_cronograma c,
			proj_cron_etapa b,
			proj_projeto p,
			man_ordem_servico a
		where	a.nr_sequencia			= nr_sequencia_p
		and		a.nr_seq_proj_cron_etapa	= b.nr_sequencia
		and		b.nr_seq_cronograma		= c.nr_sequencia
		and 	p.nr_sequencia = c.nr_seq_proj
		and		c.nr_seq_proj	= d.nr_sequencia
		and		p.ie_origem = 'D';

	end if;

	if (coalesce(nr_sequencia_w::text, '') = '') then

		select	max(b.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	proj_projeto b,
				proj_projeto_cliente_adic a
		where	a.nr_seq_projeto		= b.nr_sequencia
		and		a.NR_SEQ_ORDEM_SERV 		= nr_sequencia_p
		and		b.ie_origem = 'D';
	end if;

end if;

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
