CREATE OR REPLACE FUNCTION proj_obter_qt_total_horas_cron ( nr_seq_projeto_p bigint, nr_seq_cronograma_p bigint, ie_tipo_hora_p text, ie_tipo_cronograma_p text, ie_apenas_cron_aprov_p text default 'N') RETURNS bigint AS $body$
DECLARE


qt_total_horas_w	double precision;
qt_total_realizado_w	double precision;
qt_total_horas_saldo_w	double precision;


BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then
	begin
	select	coalesce(sum(qt_total_horas),0),
			coalesce(sum(qt_horas_realizado),0),
			coalesce(sum(qt_horas_saldo),0)
	into STRICT	qt_total_horas_w,
			qt_total_realizado_w,
			qt_total_horas_saldo_w
	from	proj_cronograma
	where	nr_seq_proj = nr_seq_projeto_p
	and	coalesce(ie_situacao, 'A') = 'A'
	and	((ie_apenas_cron_aprov_p =  'N') or (ie_apenas_cron_aprov_p = 'S' and (dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '')))
	and	ie_classificacao = 'D'
	and	((nr_sequencia = nr_seq_cronograma_p) or (coalesce(nr_seq_cronograma_p,0) = 0))
	and	ie_tipo_cronograma in (WITH RECURSIVE cte AS (

						SELECT	coalesce(trim(both regexp_substr(ie_tipo_cronograma_p,'[^,]+', 1, level)), ie_tipo_cronograma)
						
						(regexp_substr(ie_tipo_cronograma_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(ie_tipo_cronograma_p, '[^,]+', 1, level))::text <> '')
					  UNION ALL

						SELECT	coalesce(trim(both regexp_substr(ie_tipo_cronograma_p,'[^,]+', 1, level)), ie_tipo_cronograma) JOIN cte c ON ()

) SELECT * FROM cte;
);
	--and	dt_aprovacao is not null	
	if (ie_tipo_hora_p	= 'T') then
		begin
		return qt_total_horas_w;
		end;
	elsif (ie_tipo_hora_p	= 'R') then
		begin
		return qt_total_realizado_w;
		end;
	else
		begin
		return qt_total_horas_saldo_w;
		end;
	end if;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
