CREATE OR REPLACE FUNCTION proj_obter_recursos_etapa (nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);
ds_equipe_w	varchar(255);
ds_equipe_ww	varchar(255);

C01 CURSOR FOR 
	SELECT	substr(proj_obter_dados_equipe_papel(nr_seq_equipe_papel,'D'),1,255) 
	from	proj_cron_etapa_equipe 
	where	nr_seq_etapa_cron = nr_seq_etapa_p 
	and	(nr_seq_equipe_papel IS NOT NULL AND nr_seq_equipe_papel::text <> '') 
	and	((ie_recurso_externo = 'N') or (coalesce(ie_recurso_externo::text, '') = ''));


BEGIN 
 
ds_equipe_ww := '';
 
open C01;
loop 
fetch C01 into	 
	ds_equipe_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	ds_equipe_ww := substr(ds_equipe_ww || ', ' || ds_equipe_w,1,255);
	 
	end;
end loop;
close C01;
 
ds_retorno_w := substr(ds_equipe_ww,2,255);
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
