DECLARE


qt_dia_w			integer	:= 0;
qt_semana_w			integer	:= 0;


BEGIN

select	trunc(coalesce(dt_fim_real,clock_timestamp()) - coalesce(dt_inicio_real,dt_inicio_prev))
