CREATE OR REPLACE FUNCTION proj_obter_se_avaliacao_zerada (nr_seq_cliente_p bigint, cd_executor_p text) RETURNS varchar AS $body$
DECLARE



nr_seq_avaliacao_w	bigint;
ds_retorno_w		varchar(255);
qt_ponto_w		bigint;
ds_tipo_avaliacao_w	varchar(255);

c01 CURSOR FOR
SELECT	obter_valor_dominio(1326,ie_tipo_avaliacao),
	coalesce(qt_ponto,0)
from	proj_consultor_aval_res
where	nr_seq_avaliacao = nr_seq_avaliacao_w
and	ie_tipo_avaliacao in ('1','2','3');



BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_avaliacao_w
from	proj_consultor_aval
where	nr_seq_cliente = nr_seq_cliente_p
and	cd_consultor = cd_executor_p;

open c01;
	loop
	fetch c01 into	ds_tipo_avaliacao_w,
			qt_ponto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (qt_ponto_w = 0) then

	ds_retorno_w := ds_retorno_w || chr(13) || chr(10) || ds_tipo_avaliacao_w;

	end if;
	end;
	end loop;
close c01;

return	ds_retorno_w;

end;
