CREATE OR REPLACE FUNCTION proj_obter_se_etapa_atrasada (nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
dt_inicio_prev_w	proj_cron_etapa.dt_inicio_prev%type;
dt_inicio_real_w	proj_cron_etapa.dt_inicio_real%type;

BEGIN

select	max(trunc(dt_inicio_prev)),
		max(trunc(coalesce(dt_inicio_real,clock_timestamp())))
into STRICT	dt_inicio_prev_w,
		dt_inicio_real_w
from	proj_cron_etapa
where	nr_sequencia = nr_seq_etapa_p;


if (dt_inicio_prev_w < dt_inicio_real_w) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
