CREATE OR REPLACE FUNCTION proj_obter_se_excessao (dt_inicio_p timestamp, nr_seq_cronograma_p bigint) RETURNS varchar AS $body$
DECLARE


ie_excessao_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_excessao_w
from	proj_cron_excessao
where	nr_seq_cronograma = nr_seq_cronograma_p
and	trunc(dt_inicio_p,'dd') between trunc(dt_inicial,'dd') and trunc(dt_final,'dd');

return	ie_excessao_w;

end;
$body$
LANGUAGE PLPGSQL
