CREATE OR REPLACE FUNCTION proj_obter_se_grava_doc_obriga ( nm_documento_p text, nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_classif_w		bigint;
qt_doc_obriga_w		bigint;
nr_seq_documento_w	bigint;
nr_seq_classif_w		bigint;
ie_documento_w		varchar(1);

c01 CURSOR FOR
SELECT	a.nr_seq_classif
from	proj_doc_classif a
where	a.nr_seq_documento = nr_seq_documento_w
and	a.nr_estagio_proj = ie_documento_w;


BEGIN
select	coalesce(a.nr_seq_classif,0)
into STRICT	ie_classif_w
from	proj_projeto a
where	a.nr_sequencia = nr_seq_projeto_p;

select	coalesce(max(a.nr_sequencia),0),
	coalesce(max(a.nr_seq_estagio),0)
into STRICT	nr_seq_documento_w,
	ie_documento_w
from	proj_doc_obrigatorio a
where	a.ds_documento = nm_documento_p;

select	count(*)
into STRICT	qt_doc_obriga_w
from	proj_doc_classif a
where	a.nr_seq_documento = nr_seq_documento_w
and	a.nr_estagio_proj = ie_documento_w  LIMIT 1;

if (qt_doc_obriga_w = 0) then
	ds_retorno_w := 'S';
elsif (qt_doc_obriga_w > 0) then
	begin
	ds_retorno_w := 'N';
	open C01;
	loop
	fetch C01 into
		nr_seq_classif_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (nr_seq_classif_w = ie_classif_w) then
			ds_retorno_w := 'S';
		end if;
		end;
	end loop;
	close C01;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
