CREATE OR REPLACE FUNCTION proj_obter_se_lib_relat ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(4000) := '';
nr_seq_viagem_w		bigint;
cd_pessoa_fisica_w 	varchar(10);
ie_cargo_viajante_w 	varchar(1);		
 
c01 CURSOR FOR 
SELECT	a.nr_seq_viagem 
from	fin_gv_pend_aprov a, 
	via_destino b, 
	proj_projeto c, 
	via_viagem d 
where	a.nr_seq_viagem = b.nr_seq_viagem 
and	b.nr_seq_proj = c.nr_sequencia 
and	d.nr_sequencia = b.nr_seq_viagem 
--and	c.nr_seq_classif <> 19 
and	(a.dt_aprov_gv IS NOT NULL AND a.dt_aprov_gv::text <> '') 
and	d.ie_etapa_viagem = 8;
--and	nvl(a.vl_relat_desp,0) > 0; 
BEGIN 
---carga das informações de cargo 
open c01;
loop 
fetch c01 into 
	nr_seq_viagem_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	select	cd_pessoa_fisica 
	into STRICT	cd_pessoa_fisica_w 
	from	via_viagem 
	where	nr_sequencia = nr_seq_viagem_w;	
	 
	SELECT	MAX(ie_cargo) 
	INTO STRICT	ie_cargo_viajante_w 
	FROM	organograma_v 
	WHERE	cd_viajante = cd_pessoa_fisica_w;	
	 
	if (ie_cargo_viajante_w in ('F', 'L','G','D')) then 
		ds_retorno_w := ds_retorno_w || nr_seq_viagem_w || ',';
	end if;
	end;
end loop;
close c01;
 
ds_retorno_w := ' and a.nr_seq_viagem in (' || ds_retorno_w || '0)';
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
