CREATE OR REPLACE FUNCTION proj_obter_se_particip_ata ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
ie_retorno_w			varchar(1)	:= 'N';


BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	 
	cd_pessoa_fisica_w	:= substr(obter_pessoa_fisica_usuario(nm_usuario_p, 'C'),1,10);
	 
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
	 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_retorno_w 
		from	proj_ata_participante 
		where	nr_seq_ata		= nr_sequencia_p 
		and	cd_pessoa_participante	= cd_pessoa_fisica_w;
	end if;
end if;
 
 
return	ie_retorno_w;
 
end;
