CREATE OR REPLACE FUNCTION proj_obter_se_regra_liberacao ( nr_seq_projeto_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10) := 'N';
cd_pessoa_fisica_w	varchar(15);
nr_seq_gerencia_w	bigint;
nr_seq_grupo_desenv_w	bigint;


BEGIN

select 	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	usuario
where	nm_usuario = nm_usuario_p;

select	max(nr_seq_grupo)
into STRICT	nr_seq_grupo_desenv_w
from	USUARIO_GRUPO_DES
where	nm_usuario_grupo = nm_usuario_p;

select 	max(nr_seq_gerencia)
into STRICT	nr_seq_gerencia_w
from	grupo_desenvolvimento
where	nr_sequencia = nr_seq_grupo_desenv_w;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	proj_regra_visualizar
	where	((coalesce(cd_pessoa_fisica::text, '') = '') or (cd_pessoa_fisica = cd_pessoa_fisica_w))
	and	((coalesce(cd_estabelecimento::text, '') = '') or (cd_estabelecimento = cd_estabelecimento_p))
	and	((coalesce(nr_seq_gerencia::text, '') = '') or (nr_seq_gerencia = nr_seq_gerencia_w))
	and	((coalesce(nr_seq_grupo_desev::text, '') = '') or (nr_seq_grupo_desev = nr_seq_grupo_desenv_w))
	and	nr_seq_projeto = nr_seq_projeto_p;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
