CREATE OR REPLACE FUNCTION proj_obter_todas_etapas ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_atividade_w			varchar(255);
ds_atividade_total_w	varchar(255);
nr_seq_superior_w		bigint;


BEGIN

ds_atividade_w			:= '';
ds_atividade_total_w	:= '';

select	ds_atividade,
		nr_seq_superior
into STRICT	ds_atividade_w,
		nr_seq_superior_w
from	proj_cron_etapa
where	nr_sequencia = nr_sequencia_p;

ds_atividade_total_w	:= ds_atividade_total_w || ds_atividade_w;

if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
	select	ds_atividade,
			nr_seq_superior
	into STRICT	ds_atividade_w,
			nr_seq_superior_w
	from	proj_cron_etapa
	where	nr_sequencia = nr_seq_superior_w;

	ds_atividade_total_w	:= ds_atividade_w || ' - ' || ds_atividade_total_w;

	if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
		select	ds_atividade,
				nr_seq_superior
		into STRICT	ds_atividade_w,
				nr_seq_superior_w
		from	proj_cron_etapa
		where	nr_sequencia = nr_seq_superior_w;

		ds_atividade_total_w	:= ds_atividade_w || ' - ' || ds_atividade_total_w;

		if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
			select	ds_atividade,
					nr_seq_superior
			into STRICT	ds_atividade_w,
					nr_seq_superior_w
			from	proj_cron_etapa
			where	nr_sequencia = nr_seq_superior_w;

			ds_atividade_total_w	:= ds_atividade_w || ' - ' || ds_atividade_total_w;

			if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
				select	ds_atividade,
						nr_seq_superior
				into STRICT	ds_atividade_w,
						nr_seq_superior_w
				from	proj_cron_etapa
				where	nr_sequencia = nr_seq_superior_w;

				ds_atividade_total_w	:= ds_atividade_w || ' - ' || ds_atividade_total_w;
			end if;
		end if;

	end if;
end if;

return ds_atividade_total_w;

end;
$body$
