CREATE OR REPLACE FUNCTION proj_obter_total_ativ_adic ( nr_seq_etapa_p bigint) RETURNS bigint AS $body$
DECLARE


qt_min_ativ_w	double precision;


BEGIN

select	coalesce(round((dividir(sum(y.qt_min_ativ),60))::numeric,2),0)
into STRICT	qt_min_ativ_w
from 	proj_rat_ativ y
where 	y.ie_atividade_extra = 'S'
and	y.nr_seq_etapa_cron = nr_seq_etapa_p;

return	qt_min_ativ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
