CREATE OR REPLACE FUNCTION proj_obter_total_ativ_ano ( nr_seq_projeto_p bigint, dt_mes_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_min_ativ_w	double precision;


BEGIN

select	coalesce(round((dividir(sum(y.qt_min_ativ),60))::numeric,2),0)
into STRICT	qt_min_ativ_w
from 	proj_rat x,
	proj_rat_ativ y
where 	x.nr_sequencia = y.nr_seq_rat
and	x.nr_seq_proj = nr_seq_projeto_p
and	y.nr_seq_etapa_cron in (	SELECT	b.nr_sequencia
				from	proj_cronograma c,
						proj_cron_etapa b
				where (b.nr_seq_cronograma = c.nr_sequencia)
				and		c.nr_seq_proj	 = nr_seq_projeto_p
				and		coalesce(b.ie_situacao,'A') = 'A')
and (trunc(y.dt_inicio_ativ,'year') = trunc(dt_mes_p,'year') or coalesce(dt_mes_p::text, '') = '');

return	qt_min_ativ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
