CREATE OR REPLACE FUNCTION proj_obter_total_ativ_og (nr_seq_proj_p bigint, cd_executor_p text, dt_mes_p timestamp, ie_tipo_atividade_p text, nr_seq_etapa_p bigint DEFAULT NULL) RETURNS bigint AS $body$
DECLARE


/*ie_tipo_atividade_p
 Operacional ='O'
 gerencial = 'G'
 */
nr_seq_etapa_w	bigint;
qt_min_ativ_w	double precision;


BEGIN

nr_seq_etapa_w	:= coalesce(nr_seq_etapa_p,0);

SELECT	coalesce(round((dividir(SUM(y.qt_min_ativ),60))::numeric,2),0)
INTO STRICT	qt_min_ativ_w
FROM 	proj_rat x,
	proj_rat_ativ y
WHERE 	x.nr_sequencia = y.nr_seq_rat
and	((x.nr_seq_proj = nr_seq_proj_p) or (nr_seq_proj_p = 0))
AND 	((coalesce(y.ie_tipo_atividade,'O') = ie_tipo_atividade_p) or (ie_tipo_atividade_p = 'A'))
AND	x.cd_executor = cd_executor_p
AND	y.nr_seq_etapa_cron IN (	SELECT	b.nr_sequencia
				FROM	proj_cron_etapa b
				WHERE (b.nr_sequencia	= nr_seq_etapa_w OR nr_seq_etapa_w = 0)
				AND	coalesce(b.ie_situacao,'A') = 'A')

AND (TRUNC(y.dt_inicio_ativ,'month') = TRUNC(dt_mes_p,'month') OR coalesce(dt_mes_p::text, '') = '');

RETURN	qt_min_ativ_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
