CREATE OR REPLACE FUNCTION proj_obter_total_despesa ( nr_seq_projeto_p bigint, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


/* IE_TIPO_VALOR_P
	O - Or√ßado
	R - Realizado
	RN - Realizado Nossa empresa
*/
vl_retorno_w			double precision	:= 0;


BEGIN

if (ie_tipo_valor_p	= 'R') then
	select	sum(coalesce(c.vl_despesa,0))
	into STRICT	vl_retorno_w
	from	via_destino		d,
		via_despesa		c,
		via_relat_desp		b,
		via_viagem		a
	where	a.nr_sequencia		= b.nr_seq_viagem
	and	a.nr_sequencia		= d.nr_seq_viagem
	and	b.nr_sequencia		= c.nr_seq_relat
	and	d.nr_seq_proj		= nr_seq_projeto_p;
elsif (ie_tipo_valor_p	= 'RN') then
	select	sum(coalesce(c.vl_despesa,0))
	into STRICT	vl_retorno_w
	from	via_destino		d,
		via_despesa		c,
		via_relat_desp		b,
		via_viagem		a
	where	a.nr_sequencia		= b.nr_seq_viagem
	and	a.nr_sequencia		= d.nr_seq_viagem
	and	b.nr_sequencia		= c.nr_seq_relat
	and	d.nr_seq_proj		= nr_seq_projeto_p
	and	c.ie_responsavel_custo	= 'N';
elsif (ie_tipo_valor_p	= 'O') then
	select	sum(coalesce(vl_margem,0))
	into STRICT	vl_retorno_w
	from	proj_orc_desp	b,
		proj_orcamento	a
	where	a.nr_seq_proj	= nr_seq_projeto_p
	and	a.nr_sequencia	= b.nr_seq_orc;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
