CREATE OR REPLACE FUNCTION proj_obter_total_percentual ( nr_seq_projeto_p bigint) RETURNS bigint AS $body$
DECLARE


pr_retorno_w			double precision	:= 0;


BEGIN

/*select	nvl(avg(b.pr_etapa),0)
into	pr_retorno_w
from	proj_cron_etapa	b,
	proj_cronograma	a
where	a.nr_sequencia	= b.nr_seq_cronograma
and	a.nr_seq_proj	= nr_seq_projeto_p
and	a.ie_tipo_cronograma = 'E'
and	dt_aprovacao is not null; */
select	coalesce(max(PR_REALIZACAO),0)
into STRICT	pr_retorno_w
from	proj_cronograma	a
where a.nr_seq_proj	= nr_seq_projeto_p
and	a.ie_tipo_cronograma = 'E'
and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '')
and	dt_aprovacao = (SELECT max(dt_aprovacao) from proj_cronograma w
			where w.nr_seq_proj	= nr_seq_projeto_p
			and	w.ie_tipo_cronograma = 'E'
			and	(w.dt_aprovacao IS NOT NULL AND w.dt_aprovacao::text <> ''));


return	pr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
