CREATE OR REPLACE FUNCTION proj_obter_total_viagem (nr_seq_viagem_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_ignorar_cancel_p text default '', ie_ignorar_pagador_terceiro_p text default '') RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;


BEGIN

select	sum(y.vl_total) vl_total
into STRICT	vl_retorno_w
from	(	SELECT	coalesce(sum(b.vl_despesa), 0) vl_total
		from	via_despesa b,
			via_relat_desp a
		where	a.nr_sequencia	= b.nr_seq_relat
		and	b.ie_responsavel_custo <> 'N'
		and ((coalesce(ie_ignorar_pagador_terceiro_p::text, '') = '') or (ie_ignorar_pagador_terceiro_p <> ie_tipo_despesa))
		and	a.nr_seq_viagem	= nr_seq_viagem_p
		and	b.dt_despesa	between dt_inicial_p and fim_dia(dt_final_p)
		having	coalesce(sum(b.vl_despesa), 0) > 0
		
union all

		SELECT	coalesce(sum(a.vl_previsto), 0) vl_total
		from	via_transporte a
		where	a.nr_seq_viagem	= nr_seq_viagem_p
		AND	 NOT EXISTS (SELECT 1
					FROM via_reserva_transporte b
					WHERE	a.nr_sequencia	= b.nr_seq_transporte)
		having	coalesce(sum(a.vl_previsto), 0) > 0
		
union all

		select	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_hotel
		from	via_reserva a
		where	a.nr_seq_viagem	= nr_seq_viagem_p
		and	a.ie_responsavel_custo <> 'N'
		and ((coalesce(ie_ignorar_cancel_p::text, '') = '') or (coalesce(ie_cancelamento::text, '') = ''))
		and	a.ie_tipo_reserva	= 'H'
		having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0
		
union all

		select	sum(coalesce(VL_RESERVA,0)) vl_reserva
		from	via_reserva a
		where	a.nr_seq_viagem	= nr_seq_viagem_p
		and	a.ie_responsavel_custo <> 'N'
		and ((coalesce(ie_ignorar_cancel_p::text, '') = '') or (coalesce(ie_cancelamento::text, '') = ''))
		and	a.ie_tipo_reserva	= 'T'
		having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0
	) y
where	1 = 1
having	count(*) > 0;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
