CREATE OR REPLACE FUNCTION proj_obter_tot_via_geral (nr_seq_viagem_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text, ie_tipo_gasto_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;

/* ie_tipo_gasto_p_p
	D - Despesa
	T - Transporte
	H - hospedagem
	G - Geral
     ie_opcao_p
	N - Nosso
	T - Terceiro
*/
BEGIN
if (ie_tipo_gasto_p = 'G') then
	begin
	if (ie_opcao_p = 'N') then -- Nossa empresa
		begin
		select	sum(y.vl_total) vl_total
		into STRICT	vl_retorno_w
		from	(	SELECT	coalesce(sum(b.vl_despesa), 0) vl_total
				from	via_despesa b,
					via_relat_desp a
				where	a.nr_sequencia	= b.nr_seq_relat
				and	a.nr_seq_viagem	= nr_seq_viagem_p
				and	b.ie_responsavel_custo = 'N'
				and	b.dt_despesa	between dt_inicial_p and fim_dia(dt_final_p)
				having	coalesce(sum(b.vl_despesa), 0) > 0
				
union all

				SELECT	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_vaigem
				from	via_reserva a
				where	a.nr_seq_viagem	= nr_seq_viagem_p
				and	a.ie_responsavel_custo  = 'N'
				and	((to_date(to_char(a.dt_saida_prev,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_saida_prev,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p) and (to_date(to_char(a.dt_retorno_prev,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_retorno_prev,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p))
				having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0
				
union all

				select	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_hotel
				from	via_reserva a
				where	a.nr_seq_viagem	= nr_seq_viagem_p
				and	a.ie_responsavel_custo  = 'N'
				and	((to_date(to_char(a.dt_entrada_hotel,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_entrada_hotel,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p) and (to_date(to_char(a.dt_saida_hotel,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_saida_hotel,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p))
				having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0) y
		having	count(*) > 0;
		exception
		when others then
			vl_retorno_w := 0;
		end;
	elsif (ie_opcao_p = 'T') then -- Terceiro
		begin
		select	sum(y.vl_total) vl_total
		into STRICT	vl_retorno_w
		from	(	SELECT	coalesce(sum(b.vl_despesa), 0) vl_total
				from	via_despesa b,
					via_relat_desp a
				where	a.nr_sequencia	= b.nr_seq_relat
				and	a.nr_seq_viagem	= nr_seq_viagem_p
				and	b.ie_responsavel_custo = 'T'
				and	b.dt_despesa	between dt_inicial_p and fim_dia(dt_final_p)
				having	coalesce(sum(b.vl_despesa), 0) > 0
				
union all

				SELECT	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_viagem
				from	via_reserva a
				where	a.nr_seq_viagem	= nr_seq_viagem_p
				and	a.ie_responsavel_custo  = 'T'
				and	((to_date(to_char(a.dt_saida_prev,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_saida_prev,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p) and (to_date(to_char(a.dt_retorno_prev,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_retorno_prev,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p))
				having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0
				
union all

				select	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_hotel
				from	via_reserva a
				where	a.nr_seq_viagem	= nr_seq_viagem_p
				and	a.ie_responsavel_custo  = 'T'
				and	((to_date(to_char(a.dt_entrada_hotel,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_entrada_hotel,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p) and (to_date(to_char(a.dt_saida_hotel,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_saida_hotel,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p))
				having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0) y
		having	count(*) > 0;
		exception
		when others then
			vl_retorno_w := 0;
		end;
	end if;
	end;
elsif (ie_tipo_gasto_p = 'D') then
	begin
	if (ie_opcao_p = 'N') then
		begin
		select	coalesce(sum(b.vl_despesa), 0)
		into STRICT	vl_retorno_w
		from	via_despesa b,
			via_relat_desp a
		where	a.nr_sequencia	= b.nr_seq_relat
		and	a.nr_seq_viagem	= nr_seq_viagem_p
		and	b.ie_responsavel_custo = 'N'
		and	b.dt_despesa	between dt_inicial_p and fim_dia(dt_final_p)
		having	coalesce(sum(b.vl_despesa), 0) > 0;
		exception
		when others then
			vl_retorno_w := 0;
		end;
	elsif (ie_opcao_p = 'T') then
		begin
		select	coalesce(sum(b.vl_despesa), 0) vl_total
		into STRICT	vl_retorno_w
		from	via_despesa b,
			via_relat_desp a
		where	a.nr_sequencia	= b.nr_seq_relat
		and	a.nr_seq_viagem	= nr_seq_viagem_p
		and	b.ie_responsavel_custo = 'T'
		and	b.dt_despesa	between dt_inicial_p and fim_dia(dt_final_p)
		having	coalesce(sum(b.vl_despesa), 0) > 0;
		exception
		when others then
			vl_retorno_w := 0;
		end;
	end if;
	end;
elsif (ie_tipo_gasto_p = 'T') then
	begin
	if (ie_opcao_p = 'N') then
		begin
		select	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_viagem
		into STRICT	vl_retorno_w
		from	via_reserva a
		where	a.nr_seq_viagem	= nr_seq_viagem_p
		and	(a.nr_seq_meio_transp IS NOT NULL AND a.nr_seq_meio_transp::text <> '')
		and	a.ie_responsavel_custo  = 'N'
		and	((to_date(to_char(a.dt_saida_prev,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_saida_prev,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p) and
			((coalesce(a.dt_retorno_prev::text, '') = '') or
			((to_date(to_char(a.dt_retorno_prev,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_retorno_prev,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p))))
		having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0;
		exception
		when others then
			vl_retorno_w := 0;
		end;
	elsif (ie_opcao_p = 'T') then
		begin
		select	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_viagem
		into STRICT	vl_retorno_w
		from	via_reserva a
		where	a.nr_seq_viagem	= nr_seq_viagem_p
		and	(a.nr_seq_meio_transp IS NOT NULL AND a.nr_seq_meio_transp::text <> '')
		and	a.ie_responsavel_custo  = 'T'
		and	((to_date(to_char(a.dt_saida_prev,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_saida_prev,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p) and
			((coalesce(a.dt_retorno_prev::text, '') = '') or
			((to_date(to_char(a.dt_retorno_prev,'dd/mm/yyyy'),'dd/mm/yyyy') >= dt_inicial_p) and (to_date(to_char(a.dt_retorno_prev,'dd/mm/yyyy'),'dd/mm/yyyy') <= dt_final_p))))
		having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0;
		exception
		when others then
			vl_retorno_w := 0;
		end;
	end if;
	end;
elsif (ie_tipo_gasto_p = 'H') then
	begin
	if (ie_opcao_p = 'N') then
		begin
		select	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_viagem
		into STRICT	vl_retorno_w
		from	via_reserva a
		where	a.nr_seq_viagem	= nr_seq_viagem_p
		and	(a.nr_seq_hotel IS NOT NULL AND a.nr_seq_hotel::text <> '')
		and	a.ie_responsavel_custo  = 'N'
		and	a.dt_entrada_hotel between dt_inicial_p and fim_dia(dt_final_p)
		having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0;
		exception
		when others then
			vl_retorno_w := 0;
		end;
	elsif (ie_opcao_p = 'T') then
		begin
		select	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) vl_total_viagem
		into STRICT	vl_retorno_w
		from	via_reserva a
		where	a.nr_seq_viagem	= nr_seq_viagem_p
		and	(a.nr_seq_hotel IS NOT NULL AND a.nr_seq_hotel::text <> '')
		and	a.ie_responsavel_custo  = 'T'
		and	a.dt_entrada_hotel between dt_inicial_p and fim_dia(dt_final_p)
		having	coalesce(sum(obter_valor_total_hotel(nr_sequencia)),0) > 0;
		exception
		when others then
			vl_retorno_w := 0;
		end;
	end if;
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
