CREATE OR REPLACE FUNCTION proj_obter_ult_escore (nr_seq_proj_p bigint) RETURNS bigint AS $body$
DECLARE


qt_escore_w	bigint;


BEGIN

select	coalesce(max(a.qt_escore),0)
into STRICT	qt_escore_w
from	proj_escore a
where	a.nr_seq_proj		= nr_seq_proj_p
and	a.dt_escore		=
	(SELECT	max(x.dt_escore)
	from	proj_escore x
	where	x.nr_seq_proj		= nr_seq_proj_p
	and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> ''));

return	qt_escore_w;

end;
