CREATE OR REPLACE FUNCTION proj_obter_valores_orcamento ( nr_seq_orcamento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* 	IE_OPCAO_P
	1 - Receita total
	2 - Custo profissional
	3 - Margem
	4 - Margem da despesa

*/
vl_retorno_w			double precision	:= 0;
vl_receita_total_w		double precision	:= 0;
vl_custo_total_w		double precision	:= 0;
vl_margem_w			double precision	:= 0;
vl_desp_margem_w		double precision	:= 0;


BEGIN

select	sum(coalesce(vl_receita_total,0)),
	sum(coalesce(vl_custo_total,0)),
	sum(coalesce(vl_margem,0))
into STRICT	vl_receita_total_w,
	vl_custo_total_w,
	vl_margem_w
from	proj_orc_prof
where	nr_seq_orc	= nr_seq_orcamento_p;

select	sum(coalesce(vl_margem,0))
into STRICT	vl_desp_margem_w
from	proj_orc_desp
where	nr_seq_orc	= nr_seq_orcamento_p;

if (ie_opcao_p	= 1) then
	vl_retorno_w	:= vl_receita_total_w;
elsif (ie_opcao_p	= 2) then
	vl_retorno_w	:= vl_custo_total_w;
elsif (ie_opcao_p	= 3) then
	vl_retorno_w	:= vl_margem_w;
elsif (ie_opcao_p	= 4) then
	vl_retorno_w	:= vl_desp_margem_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
