SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION proj_obter_valor_hora_orc ( nr_seq_nivel_cons_p bigint, cd_executor_p text, nr_seq_orc_p bigint, ie_tipo_valor_p bigint) RETURNS bigint AS $body$
DECLARE

 
/* ie_tipo_valor_p 
	1 - Valor a pagar 
	2 - Valor a receber 
*/
 
 
vl_retorno_w			double precision	:= 0;
vl_hora_pagar_w			double precision	:= 0;
vl_hora_receber_w		double precision	:= 0;
nr_seq_proj_w			bigint;


BEGIN 
 
select	coalesce(max(nr_seq_proj),0) 
into STRICT	nr_seq_proj_w 
from	proj_orcamento 
where	nr_sequencia	= nr_seq_orc_p;
 
if (coalesce(nr_seq_nivel_cons_p,0) > 0) then 
	select	coalesce(max(a.vl_hora_pagar),0), 
		coalesce(max(a.vl_hora_cobrar),0) 
	into STRICT	vl_hora_pagar_w, 
		vl_hora_receber_w 
	from	proj_consultor_nivel	b, 
		proj_rat		a 
	where	a.cd_executor	= b.cd_consultor 
	and	a.nr_seq_proj	= nr_seq_proj_w 
	and	b.nr_seq_nivel	= nr_seq_nivel_cons_p;
elsif (cd_executor_p <> 'X') then 
	select	coalesce(max(vl_hora_pagar),0) 
	into STRICT	vl_hora_pagar_w 
	from	proj_rat_v 
	where	nr_seq_proj	= nr_seq_proj_w 
	and	cd_executor	= cd_executor_p 
	and	nr_sequencia in (	SELECT	coalesce(max(nr_sequencia),0) 
					from	proj_rat_v 
					where	nr_seq_proj	= nr_seq_proj_w 
					and	cd_executor	= cd_executor_p 
					and   vl_hora_pagar	> 0);
					 
	select	coalesce(max(vl_hora_cobrar),0) 
	into STRICT	vl_hora_receber_w 
	from	proj_rat_v 
	where	nr_seq_proj	= nr_seq_proj_w 
	and	cd_executor	= cd_executor_p 
	and	nr_sequencia in (	SELECT	coalesce(max(nr_sequencia),0) 
					from	proj_rat_v 
					where	nr_seq_proj	= nr_seq_proj_w 
					and	cd_executor	= cd_executor_p 
					and   vl_hora_cobrar	> 0);
end if;
 
if (ie_tipo_valor_p	= 1) then 
	vl_retorno_w	:= vl_hora_pagar_w;
elsif (ie_tipo_valor_p	= 2) then 
	vl_retorno_w	:= vl_hora_receber_w;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
