CREATE OR REPLACE FUNCTION proj_obter_valor_margem_rat ( nr_seq_rat_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision	:= 0;
vl_receita_total_w		double precision	:= 0;
vl_despesa_w			double precision	:= 0;


BEGIN

select	sum(coalesce(vl_cobrar_cliente,0)) vl_receita_total,
	sum(coalesce(vl_pagar,0) + coalesce(vl_imposto,0) + coalesce(vl_adicional_clt,0) + coalesce(vl_custo_inst,0)) vl_despesa
into STRICT	vl_receita_total_w,
	vl_despesa_w
from	proj_rat
where	nr_sequencia	= nr_seq_rat_p;

vl_retorno_w	:= vl_receita_total_w - vl_despesa_w;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
