CREATE OR REPLACE FUNCTION proj_obter_valor_orcado_mes ( dt_referencia_p timestamp, nr_seq_proj_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	bigint;


BEGIN

select	coalesce(Sum(b.qt_profissional * b.qt_hora_sem),0) qt_semanal
into STRICT	vl_retorno_w
from	proj_orcamento a,
	proj_orc_prof b
where	a.nr_sequencia = b.nr_seq_orc
and	a.nr_seq_proj = nr_seq_proj_p
and	dt_referencia_p BETWEEN a.dt_orcamento AND PKG_DATE_UTILS.ADD_MONTH(a.dt_orcamento,b.qt_semana, 0);

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
