CREATE OR REPLACE FUNCTION proj_obter_variacao_etapa ( nr_seq_etapa_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;


BEGIN

select	round((dividir((coalesce(qt_hora_real,0) * 100), coalesce(qt_hora_prev,0)) - 100),2)
into STRICT	vl_retorno_w
from	proj_cron_etapa
where	nr_sequencia	= nr_seq_etapa_p;

if (vl_retorno_w < 0) then
	vl_retorno_w	:= 0;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
