CREATE OR REPLACE FUNCTION prp_soma_horas_proj_etapa (nr_seq_proj_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	proj_cronograma.qt_total_horas%type;


BEGIN

if (nr_seq_proj_p IS NOT NULL AND nr_seq_proj_p::text <> '') then
	SELECT	SUM(e.QT_TOTAL_HORAS)
	into STRICT	qt_retorno_w
	FROM	proj_cronograma e,
		prp_processo_fase p,
		prp_fase_processo f
	WHERE	p.nr_Sequencia = e.nr_seq_processo_fase
	AND	f.nr_sequencia = p.nr_seq_fase_processo
	AND	e.nr_seq_proj = nr_seq_proj_p;
else
	qt_retorno_w := 0;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
