CREATE OR REPLACE FUNCTION pr_obter_tipo_pac_reab (NR_TIPO_PAC_REAB_P text, CD_PESSOA_FISICA_P text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10);


BEGIN
if (NR_TIPO_PAC_REAB_P IS NOT NULL AND NR_TIPO_PAC_REAB_P::text <> '' AND CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT 	ds_retorno_w
	from 	RP_LISTA_ESPERA_MODELO a,
		RP_PACIENTE_REABILITACAO b,
		RP_TIPO_PAC_REAB c
	where 	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and 	c.NR_SEQ_PAC_REAB = b.nr_sequencia
	and  	c.NR_TIPO_PAC_REAB = NR_TIPO_PAC_REAB_P
	and  	((a.CD_PESSOA_FISICA = CD_PESSOA_FISICA_P) or ( CD_PESSOA_FISICA_P = '0'));
end if;




return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
