CREATE OR REPLACE FUNCTION ps_obter_classif_conta (ie_classificacao_p text) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w	varchar(255);


BEGIN
if (ie_classificacao_p IS NOT NULL AND ie_classificacao_p::text <> '') then
	begin
	if (ie_classificacao_p = 'C') then
		begin
		ds_classificacao_w	:= 'Costumer';
		end;
	else
		begin
		ds_classificacao_w	:= obter_valor_dominio(1315, ie_classificacao_p);
		end;
	end if;
	end;
end if;
return ds_classificacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
