CREATE OR REPLACE FUNCTION ps_obter_forecasting_quarter (dt_forecasting_p timestamp) RETURNS bigint AS $body$
DECLARE


forecasting_quarter_w	smallint;


BEGIN
if (dt_forecasting_p IS NOT NULL AND dt_forecasting_p::text <> '') then
	begin
	if ((to_char(dt_forecasting_p,'mm'))::numeric  <= 3) then
		begin
		forecasting_quarter_w := 1;
		end;
	elsif ((to_char(dt_forecasting_p,'mm'))::numeric  <= 6) then
		begin
		forecasting_quarter_w := 2;
		end;
	elsif ((to_char(dt_forecasting_p,'mm'))::numeric  <= 9) then
		begin
		forecasting_quarter_w := 3;
		end;
	else
		begin
		forecasting_quarter_w := 4;
		end;
	end if;
	end;
end if;
return forecasting_quarter_w;
end;
