CREATE OR REPLACE FUNCTION ps_obter_gestor_conta ( nr_seq_cliente_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

		 
ds_gestor_w	varchar(60);
nr_seq_gestor_w	bigint;
cd_gestor_w	varchar(10);
		

BEGIN 
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then 
	begin 
	select	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_gestor_w 
	from	com_cliente_gestor 
	where	nr_seq_cliente = nr_seq_cliente_p 
	and	coalesce(dt_final::text, '') = '';
	 
	if (nr_seq_gestor_w > 0) then 
		begin 
		select	cd_pessoa_fisica, 
			substr(obter_nome_pf(cd_pessoa_fisica),1,60) 
		into STRICT	cd_gestor_w, 
			ds_gestor_w 
		from	com_cliente_gestor 
		where	nr_sequencia = nr_seq_gestor_w;
		 
		if (ie_opcao_p = 'C') then 
			begin 
			ds_gestor_w := cd_gestor_w;
			end;
		end if;
		end;
	end if;
	end;
end if;
return ds_gestor_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
