CREATE OR REPLACE FUNCTION ps_obter_pgto_prop_venda_dec ( nr_seq_cliente_p bigint, ie_valor_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_tipo_item_w	bigint;
ds_pgto_ww		varchar(4000);
ds_pgto_w		varchar(4000);
qt_tam_inf_w		bigint;

c01 CURSOR FOR
SELECT	i.nr_seq_tipo_item,
	i.ds_forma_pagto
from	com_cliente_prop_item i,
	com_cliente_proposta p
where	i.nr_seq_proposta = p.nr_sequencia
and	p.nr_seq_cliente = nr_seq_cliente_p
and	(p.dt_liberacao IS NOT NULL AND p.dt_liberacao::text <> '')
and	coalesce(p.dt_cancelamento::text, '') = ''
and	coalesce(p.dt_vencimento,clock_timestamp()) >= clock_timestamp()
and	coalesce(i.ie_orcam_coml_wheb,'N') = 'S'
and	(i.ds_forma_pagto IS NOT NULL AND i.ds_forma_pagto::text <> '');


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') and (ie_valor_p IS NOT NULL AND ie_valor_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nr_seq_tipo_item_w,
			ds_pgto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		qt_tam_inf_w := coalesce(length(ds_pgto_ww),0) + coalesce(length(ds_pgto_w),0) + 2;

		if (ie_valor_p = 'CDU') and (nr_seq_tipo_item_w in (12,5,10,13)) then
			begin
			if (qt_tam_inf_w < 4000) then
				begin
				if (coalesce(ds_pgto_ww::text, '') = '') then
					begin
					ds_pgto_ww := ds_pgto_w;
					end;
				else
					begin
					ds_pgto_ww := ds_pgto_ww || '; ' || ds_pgto_w;
					end;
				end if;
				end;
			end if;
			end;
		elsif (ie_valor_p = 'LUT') and (nr_seq_tipo_item_w in (11,1,9,14)) then
			begin
			if (qt_tam_inf_w < 4000) then
				begin
				if (coalesce(ds_pgto_ww::text, '') = '') then
					begin
					ds_pgto_ww := ds_pgto_w;
					end;
				else
					begin
					ds_pgto_ww := ds_pgto_ww || '; ' || ds_pgto_w;
					end;
				end if;
				end;
			end if;
			end;
		elsif (ie_valor_p = 'MP') and (nr_seq_tipo_item_w = 7) then
			begin
			if (qt_tam_inf_w < 4000) then
				begin
				if (coalesce(ds_pgto_ww::text, '') = '') then
					begin
					ds_pgto_ww := ds_pgto_w;
					end;
				else
					begin
					ds_pgto_ww := ds_pgto_ww || '; ' || ds_pgto_w;
					end;
				end if;
				end;
			end if;
			end;
		elsif (ie_valor_p = 'P') and (nr_seq_tipo_item_w in (2,4,3)) then
			begin
			if (qt_tam_inf_w < 4000) then
				begin
				if (coalesce(ds_pgto_ww::text, '') = '') then
					begin
					ds_pgto_ww := ds_pgto_w;
					end;
				else
					begin
					ds_pgto_ww := ds_pgto_ww || '; ' || ds_pgto_w;
					end;
				end if;
				end;
			end if;
			end;
		elsif (ie_valor_p = 'O') and (nr_seq_tipo_item_w not in (12,5,10,13,11,1,9,14,7,2)) then
			begin
			if (qt_tam_inf_w < 4000) then
				begin
				if (coalesce(ds_pgto_ww::text, '') = '') then
					begin
					ds_pgto_ww := ds_pgto_w;
					end;
				else
					begin
					ds_pgto_ww := ds_pgto_ww || '; ' || ds_pgto_w;
					end;
				end if;
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;
return ds_pgto_ww;
end;
