CREATE OR REPLACE FUNCTION ps_obter_quant_propostas_venda ( nr_seq_cliente_p bigint) RETURNS bigint AS $body$
DECLARE


qt_propostas_w	double precision := 0;


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_propostas_w
	from	com_cliente_prop_item i,
		com_cliente_proposta p
	where	i.nr_seq_proposta = p.nr_sequencia
	and	p.nr_seq_cliente = nr_seq_cliente_p
	and	(p.dt_liberacao IS NOT NULL AND p.dt_liberacao::text <> '')
	and	coalesce(p.dt_cancelamento::text, '') = ''
	and	coalesce(p.dt_vencimento,clock_timestamp()) >= clock_timestamp()
	and	coalesce(i.ie_orcam_coml_wheb,'N') = 'S';
	end;
end if;
return qt_propostas_w;
end;
